definition.script:
pipeline {
    agent any

    stages {
        stage('Checkout Code') {
            steps {
                checkout([$class: 'GitSCM', branches: [[name: "*/${params.branch}"]], doGenerateSubmoduleConfigurations: false, extensions: [[$class: 'CloneOption', noTags: true, reference: '', shallow: false]], submoduleCfg: [], userRemoteConfigs: [[credentialsId: 'GitHub account for Nable', url: params.gitHubRepoUrl]]])
            }
        }

        stage('Start SonarQube Scan') {
                environment { 
                    SONAR_LOGIN = credentials('SONAR_LOGIN') 
                }
                tools {
                    maven 'MAVEN_INSTALLATION'
                }
                steps {
                    sh "mvn clean compile sonar:sonar -Dsonar.host.url=${params.sonarHostUrl} -Dsonar.login=$SONAR_LOGIN -Dsonar.projectKey=${params.sonarProjectKey} -Dmaven.test.skip=true"
                } 
        }
    }
}
